import axios from "axios"
import NewsItemType from "../types/NewsItem"

interface bodyPost{
    description: string;
    subDescription?: [string, string?, string?];
}

class axiosReq{
    async GET(url: string): Promise<NewsItemType[]> {
        const resp = await axios.get(url)
        return resp.data as NewsItemType[]
    }
    async GetOne(url: string, id: string): Promise<NewsItemType>{
        const resp = await axios.get(url + '/' + id)
        return resp.data as NewsItemType
    }
    async POST(url: string, body: bodyPost, imageFile: File){
        const formData = new FormData();
        formData.append('description', body.description);
        formData.append('subDescription', JSON.stringify(body.subDescription));
        formData.append('image', imageFile);

        await axios.post(url, formData)
          .then(response => {
            console.log(response);
          })
          .catch(error => {
            console.log(error);
          });
    }
}

export default new axiosReq()
