import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import NewsItemType from "../../types/NewsItem";

const initialState = {
    value: [],
    status: 'idle',
};
// /api/news
export class axiosReq{
    async GET(url: string): Promise<NewsItemType[]> {
        const resp = await axios.get(url)
        return resp.data as NewsItemType[]
    }
}
export const axiosHTTP = new axiosReq()

export const widnowWidthSlice = createSlice({
    name: 'newsList',
    initialState,
    reducers: {

    }
})

// export const { resize } = widnowWidthSlice.actions;

export const selectWidth = (state: any) => state.windowWidth.value;

export default widnowWidthSlice.reducer;
