import { createSlice } from "@reduxjs/toolkit";


export interface WHObject{
    name: string;
    width: {w1: number,w2: number ,w3: number,w4: number,w5: number};
    height: {h1: number,h2: number ,h3: number,h4: number,h5: number};
}



const initialState = {
    value: 0,
};

export const widnowWidthSlice = createSlice({
    name: 'windowWidth',
    initialState,
    reducers: {
        resize: (state) => {
            state.value = window.innerWidth
        }
    }
})

export const { resize } = widnowWidthSlice.actions;

export const selectWidth = (state: any) => state.windowWidth.value;

export default widnowWidthSlice.reducer;
