import { createSlice } from "@reduxjs/toolkit";


export interface WHObject{
    name: string;
    width: {w1: number,w2: number ,w3: number,w4: number,w5: number};
    height: {h1: number,h2: number ,h3: number,h4: number,h5: number};
}

export const TriggerWH = [
    ///////////////////
    {name: 'защита', 
    width:
    {
        w1: 130, w2: 100,
        w3: 58, w4: 58,
        w5: 58
    },
    height :
    {
        h1: 157, h2: 121,
        h4: 70, h3: 70,
        h5: 70
    }
    },
    ///////////////////
    {name: 'твоё Будущее',
    width:
    {
        w1: 158, w2: 116,
        w3: 60, w4: 60,
        w5: 60
    },
    height :
    {
        h1: 158, h2: 116,
        h4: 60, h3: 60,
        h5: 60
    }
    },
    ///////////////////
    {name: 'Бизнес предложения',

    width:
    {
        w1: 187, w2: 118,
        w3: 65 , w4: 65,
        w5: 65
    },
    height :
    {
        h1: 157, h2: 99,
        h4: 49, h3: 49,
        h5: 49
    }
    },
]

export const InsuranceWH = [
    ///////////////////
    {name: 'ДМС', 
    width:
    {
        w1: 130, w2: 100,
        w3: 58, w4: 58,
        w5: 58
    },
    height :
    {
        h1: 157, h2: 121,
        h4: 70, h3: 70,
        h5: 70
    }
    },
    ///////////////////
    {name: '«Здоровье»',
    width:
    {
        w1: 158, w2: 116,
        w3: 60, w4: 60,
        w5: 60
    },
    height :
    {
        h1: 158, h2: 116,
        h4: 60, h3: 60,
        h5: 60
    }
    },
    ///////////////////
    {name: 'Бизнес предложения',

    width:
    {
        w1: 187, w2: 118,
        w3: 65 , w4: 65,
        w5: 65
    },
    height :
    {
        h1: 157, h2: 99,
        h4: 49, h3: 49,
        h5: 49
    }
    },
]

const initialState = {
    value: 0,
    status: 'idle',
};

export const widnowWidthSlice = createSlice({
    name: 'windowWidth',
    initialState,
    reducers: {
        resize: (state) => {
            state.value = window.innerWidth
        }
    }
})

export const { resize } = widnowWidthSlice.actions;

export const selectWidth = (state: any) => state.windowWidth.value;

export default widnowWidthSlice.reducer;
