import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    value: 0,
    status: 'idle',
};

export const scrollSlice = createSlice({
    name: 'scroll',
    initialState,
    reducers: {
        scrolling: (state) => {
            state.value = document.body.scrollTop
        }
    }
})

export const { scrolling } = scroll.actions;

export const selectWidth = (state: any) => state.scroll.value;

export default scroll.reducer;
