import { createSlice } from "@reduxjs/toolkit";

interface initalStateInterface{
    value: boolean;
}

const initialState: initalStateInterface = {
    value: false
};

export const registrationSlice = createSlice({
    name: 'registration',
    initialState,
    reducers: {
        registrate: (state) => {
            state.value = state.value ? false : true
        }
    }
})

export const { registrate } = registrationSlice.actions;

export const selectReg = (state: any) => state.registration.value;

export default registrationSlice.reducer;
