import React, { FC, useEffect, useState } from 'react';
import ValidationType from '../../../types/Validation';
import './FormInput.css'

interface FormInputProps{
    placeholder: string;
    title: string;
    errorMessage?: string;
    isPassword?: boolean;
    isInfo?: boolean;
    isTel?: boolean;
    validation?: ValidationType;
    width?: 'normal' | 'small' | 'large'
    indexOne?: true;
}

const FormInput: FC<FormInputProps> = ({
    placeholder,
    isTel = false,
    title,
    errorMessage,
    isPassword = false,
    isInfo = false,
    width = 'normal',
    indexOne = false
}) => {
    const [passwordShow, setPasswordShow] = useState <boolean>(false)
    const [isError, setIsError] = useState <boolean>(false)
    const [inputWidth, setInputWidth] = useState <number>(0)
    const [marginTop, setMarginTop] = useState <number>(0)
    const [telNum, setTelNum] = useState <string>('+7(___)___-__-_')

    const passwordShowHandler = () => {
        if(passwordShow){
            setPasswordShow(false)
            return
        }
        setPasswordShow(true)
    }
    const telNumHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
        let inputValue = e.target.value

        if(inputValue.length - 1 < 16 && Number(inputValue[inputValue.length - 1]) > 0){
            let copyInputValue = inputValue.split('')
            copyInputValue.every((cv, i, arr) => {
                if(cv === '_') {
                    arr[i] = inputValue[inputValue.length - 1]
                    arr[inputValue.length - 1] = ''
                    return false
                }
                else return true
            })

            let newVal = copyInputValue.join('')
            setTelNum(newVal)
        }
        else{

        }

    }
    const getMarginTop = () => {
        if(!indexOne){
            if(isError){
                setMarginTop(29)
            }
            else{
                setMarginTop(53)
            }
        }
    }
    const getWidth = () => {
        if(inputWidth === 0){
            let decrementWidth = 0
            if(isPassword){
                decrementWidth = 20
            }
            switch(width){
                case 'normal':
                    setInputWidth(265 - decrementWidth)
                    break
                case 'small':
                    setInputWidth(165 - decrementWidth)
                    break
                case 'large':
                    setInputWidth(365 - decrementWidth)
                    break
            }
        }
    }

    useEffect(() => {
        getWidth()
        getMarginTop()
    }, [])
    return (
        <div className='FormInput' style={{marginTop: marginTop}}   >
            <div className="FormInput__title">{title}</div>
            <div className={isError ? 'FormInput__bar_error' : 'FormInput__bar'} >
                <input
                style={{width: inputWidth}}
                type="text"
                onChange={telNumHandler}
                value={isTel ? telNum : ''}
                className='FormInput_input'
                placeholder={isTel ? '+7(___)__-__-__' : placeholder}
                />
                {
                    isPassword ? 
                    <div className={passwordShow ? 'password_show' : 'password_hide'}
                    onClick={passwordShowHandler}
                    ></div>
                    :
                    <div></div>
                }
                {
                    isInfo ?
                    <div className='info'></div>
                    :
                    <div></div>
                }
            </div>
            {
                isError ?
                errorMessage 
                    ? 
                        <div className='FormInput__errorMessage'>{errorMessage}</div>
                    :
                        <div className='FormInput__errorMessage'>{'Введите' + title.toLowerCase}</div>
                :
                <div></div>
            }
        </div>
    );
};

export default FormInput;
