import React from 'react';
import { useLoaderData, useNavigation } from 'react-router-dom';
import NewsItemType from '../types/NewsItem';
import axiosRequests from '../classes/axiosRequests';
import Loader from '../components/UI/Loader/Loader';

const NewsPage = () => {
    const OneNews = useLoaderData() as NewsItemType[]
    const navigation = useNavigation()
    navigation.state = 'loading'
    if(navigation.state === 'loading'){
        return (
            <div className="_container-1">
               <div className="Loader-wrapper">
                    <Loader size='large' />
               </div>
            </div>
        )
    }
    return (
        <div className='NewsPage'>
            <div className="_container-1">
                {OneNews[0].description}
            </div>
        </div>
    );
};

export const NewsPageLoader = async () => {
    await setTimeout(() => {}, 1000)
    const resp = axiosRequests.GET('/api/news')
    .then(response => {
        console.log(response)
        return response
    })
    .catch(e => console.log(e))
    return resp
}



export default NewsPage;
