import React, { useState } from 'react';
import './styles/SpecialOffers/SpecialOffers.css'
import OfferItemType from '../types/OfferItem';

import { Swiper, SwiperSlide } from 'swiper/react'
import { Scrollbar } from 'swiper';
import 'swiper/swiper.css'
import 'swiper/css/scrollbar'
import './styles/NewsSlider/NewsSlider.css'
import { useSelector } from 'react-redux';
import { selectWidth } from './globalSlices/windowWidthSlice';
import OffersItem from './OffersItem';

const SpecialOffers = () => {
    const windowWidth = useSelector(selectWidth) as number
    const [offersList, setOffersList] = useState <OfferItemType[]>([])

    return (
        <div>
             <Swiper
                style={{height: '650px'}}
                modules={[Scrollbar]}
                spaceBetween={ windowWidth > 800 ? 20 : windowWidth > 700 ? 100
                     : windowWidth > 620 ? -200 : windowWidth > 580 ? -150 : windowWidth > 500 ? 70 : windowWidth > 380 ?
                     50 : 100}
                slidesPerView={windowWidth > 700 ? 2 : 1}
                scrollbar={{draggable: true}}
            >
                {
                    offersList.map((item: OfferItemType, index: number) => 
                        <SwiperSlide>
                            <OffersItem
                                _id={item._id}
                                key={index + 1}
                                description={item.description}
                                image={item.image}
                            />
                        </SwiperSlide>
                    )
                }
            </Swiper>
        </div>
    );
};

export default SpecialOffers;
