import React, { FC, useState } from 'react';
import './Trigger.css'
import Button, { ButtonBorderVariant, ButtonTextVariant } from './UI/Button/Button';

interface TriggerProps{
    imgPath: string;
    title: string;
    buttonText: string;
}

const Trigger: FC<TriggerProps> = ({imgPath, title, buttonText}) => {
    const [isHover, setIsHover] = useState(false)

    const mouseOverHandler = (e: React.MouseEvent<HTMLDivElement>) => {
        setIsHover(true)
    }
    const mouseOutHandler = (e: React.MouseEvent<HTMLDivElement>) => {
        setIsHover(true)
    }

    return (
        <div className='Trigger' onMouseOver={mouseOverHandler} onMouseOut={mouseOutHandler}>
            <h1 className="Trigger__title">{title}</h1>
            <div className="Trigger__image-cont">
                <img src={imgPath} alt="f" />   
            </div>
            <div className="buttons" >
                <Button 
                    height={50}
                    width={255}
                    children = 'buttonText' 
                    variantBorder={ButtonBorderVariant.black}
                    variantText={ButtonTextVariant.black}
                />
            </div>
        </div>
    );
};

export default Trigger;
