import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    value: 0,
};

export const counterSlice = createSlice({
    name: 'counter',
    initialState,
    reducers: {
        resize: (state) => {
            state.value = <number>window.innerWidth
        }
    }
})

export const { resize } = counterSlice.actions;

export default counterSlice.reducer;
