import React, { FC } from 'react';
import './Title.css'

interface TitleProps{
    children: string | React.ReactNode;
    hType: string | JSX.Element;
    fontSize?: number ;
}

const Title: FC<TitleProps> = ({children, hType, fontSize}) => {
    return (
        <>
        {   hType === 'h1' ? <h1 style={{fontSize: fontSize}}>{children}</h1>:
            hType === 'h2' ? <h2 style={{fontSize: fontSize}}>{children}</h2> :
            hType === 'h3' ? <h3 style={{fontSize: fontSize}}>{children}</h3> : 
            hType === 'h4' ? <h4 style={{fontSize: fontSize}}>{children}</h4> : hType
        }
        </>
    );
};

export default Title;
