class isHover{
    isHoverState: boolean = false

    mouseOverHandler = (e: React.MouseEvent<HTMLDivElement>) => {
        this.isHoverState = true
    }
    mouseOutHandler = (e: React.MouseEvent<HTMLDivElement>) => {
        this.isHoverState = false
    }
}

export default new isHover()
