import React, { FC, useState } from 'react';
import NewsItemType from '../types/NewsItem';
import { Row, Col } from 'react-bootstrap';
import NewsItem from './NewsItem';

interface NewsListBSprops{
    NewsList: NewsItemType[];
}

const NewsListBS:FC<NewsListBSprops> = ({NewsList}) => {
    const [cols, setCols] = useState <number>(0)
    return (
        <div className='NewsListBS'>
                {   
                    NewsList.length !== 0 ?
                    NewsList.slice(0, NewsList.length / 3).map((item: NewsItemType, index: number) => {
                        const sliceElements = 3
                        let counter: number = 3 * (0 + 1 + index)

                        return (
                            <Row>
                            {NewsList.slice(counter, sliceElements).map((item: NewsItemType, index: number) => 
                            <Col>
                                <NewsItem
                                style={{marginLeft: "0px"}}
                                key={item._id}
                                image={item.image}
                                _id={item._id}
                                date={item.date}
                                description={item.description}
                                subDescription={item.subDescription}
                                />
                            </Col>

                            )}
                        </Row>
                        )
                    })








                    :
                    <div className='NoNews'>Список новостей пуст...</div> 
                }

        </div>
    );
};

export default NewsListBS;
