import React, { FC, useEffect, useState } from 'react';
import './styles/Trigger.css'
import Button, { ButtonBorderVariant, ButtonTextVariant } from './UI/Button/Button';


interface sizesArray{
    sizeName: string;
    size: number;
}

interface titleArrInterface{
    titss: string[];
    sizes: sizesArray[]
}
interface TriggerProps{
    imgPath: string;
    title: string;
    buttonText: string;
    buttonTextTwo?: string;
    titles: string[];
}

const Trigger: FC<TriggerProps> = ({imgPath, title, buttonText, buttonTextTwo, titles}) => {
    const [isHover, setIsHover] = useState(false)
    const [windowWidth, setWindowWidth] = useState <number>(window.innerWidth)
    const [counter6, setCounter6] = useState <number>(0)

    const mouseOverHandler = (e: React.MouseEvent<HTMLDivElement>) => {
        setIsHover(true)
    }
    const mouseOutHandler = (e: React.MouseEvent<HTMLDivElement>) => {
        setIsHover(false)
    }
    window.addEventListener('resize', () => {
        setWindowWidth(window.innerWidth)
    }, true);

    return (
        <div className='TriggerContainer'>
            <div
                className={isHover ? 'TriggerDirty' : 'Trigger'}
                onMouseOver={mouseOverHandler}
                onMouseOut={mouseOutHandler}
            >
                <h1 className="Trigger__title"
                style={{marginTop: title.trim().split(/\s+/).length >= 2 ? '50px' : '74px'}}
                >{title}</h1>
                <div
                className="Trigger__image-cont"
                style={{width: windowWidth <= 1000 && windowWidth > 768 && title === 'защита' ?
                100 : windowWidth <= 1000 && windowWidth > 768 && title === 'твоё Будущее' ? 116 :
                windowWidth <= 1000 && windowWidth > 768 && title === 'Бизнес предложения' ?
                118 : windowWidth <= 768 && windowWidth > 480 &&  title === 'защита' ? 100 : 
                windowWidth <= 768 && windowWidth > 480 && title === 'твоё Будущее' ?
                116 : windowWidth <= 768 && windowWidth > 480 && title === 'Бизнес предложения' ? 118 :
                windowWidth <= 480 && windowWidth > 320 && title === 'защита' ?
                58 : windowWidth <= 480 && windowWidth > 320 && title === 'твоё Будущее' ? 60 : 
                windowWidth <= 480 && windowWidth > 320 && title === 'Бизнес предложения' ? 58 :
                title === 'защита' && windowWidth <= 320 ? 58 :
                title === 'твоё Будущее' && windowWidth <= 320 ? 60 :
                title === 'Бизнес предложения' && windowWidth <= 58 ? 
                100 : title === 'защита' ? 130 : title === 'твоё Будущее' ?
                158 : title === 'Бизнес предложения' ? 187 : 0,
                // Костыль!
                height: windowWidth <= 1000 && windowWidth > 768 && title === 'защита' ?
                121 : windowWidth <= 1000 && windowWidth > 768 && title === 'твоё Будущее' ? 116 :
                windowWidth <= 1000 && windowWidth > 768 && title === 'Бизнес предложения' ?
                99 : windowWidth <= 768 && windowWidth > 480 &&  title === 'защита' ? 121 : 
                windowWidth <= 768 && windowWidth > 480 && title === 'твоё Будущее' ?
                116 : windowWidth <= 768 && windowWidth > 480 && title === 'Бизнес предложения' ? 99 :
                windowWidth <= 480 && windowWidth > 320 && title === 'защита' ?
                70 : windowWidth <= 480 && windowWidth > 320 && title === 'твоё Будущее' ? 60 : 
                windowWidth <= 480 && windowWidth > 320 && title === 'Бизнес предложения' ? 49 :
                title === 'защита' && windowWidth <= 320 ? 70 :
                title === 'твоё Будущее' && windowWidth <= 320 ? 60 :
                title === 'Бизнес предложения' && windowWidth <= 49 ? 
                100 : title === 'защита' ? 158 : title === 'твоё Будущее' ?
                158 : title === 'Бизнес предложения' ? 151 : 0}} 
                >
                    <img src={imgPath} alt="f" />   
                </div>
                <div className="buttons" style={{marginTop: buttonTextTwo ? '63px' : '96px'}}>
                    <>
                        <Button
                            text={buttonText}
                            height={50}
                            width={255}
                            variantBorder={isHover ? ButtonBorderVariant.dirty : ButtonBorderVariant.black}
                            variantText={isHover ? ButtonTextVariant.dirty : ButtonTextVariant.black}
                        />
                        {
                        buttonTextTwo ?
                        <div style={{marginTop: '31px'}}>
                            <Button
                            text={buttonTextTwo}
                            height={50}
                            width={255}
                            variantBorder={isHover ? ButtonBorderVariant.dirty : ButtonBorderVariant.black}
                            variantText={isHover ? ButtonTextVariant.dirty : ButtonTextVariant.black}
                            />
                        </div>
                        : () => {}
                        }
                    </>
                </div>
            </div>
        </div>
    );
};

export default Trigger;
