import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { selectWidth } from './storeSlices/windowWidthSlice';
import NewsSlider from './NewsSlider';
import NewsWithoutSlider from './NewsWithoutSlider';
import './styles/News/News.css'
import Title from './UI/Title/Title';
import NewsItemType from '../types/NewsItem';
import { fetchAllNews } from './storeSlices/newsSlice/newsAPI';
import { AnyAction, ThunkDispatch } from '@reduxjs/toolkit';
import { selectNews } from './storeSlices/newsSlice/newsSlice';

const News = () => {
    const dispatch: ThunkDispatch<{}, {}, AnyAction> = useDispatch()
    const windowWidth = useSelector(selectWidth) as number
    const [NewsList, setNewsList] = useState <NewsItemType[]>([])

    const as = async () => {
        console.log(selectNews)
        await dispatch(fetchAllNews('/api/news'))
        console.log(selectNews)

    }
    useEffect(() => {
        // axiosRequests.GET<NewsItemType>('/api/news')
        // .then(response => setNewsList(response))
        // .catch(e => console.log(e))
        as()
    }, [])

    return (
        <div className='News'>
             <Title hType='h1'>новости</Title>
            {
                windowWidth > 1000 ?
                <NewsWithoutSlider NewsList={NewsList} /> :
                <NewsSlider NewsList={NewsList.slice(0, 30)}/>
            } 
        </div>
    );
};

export default News;
