import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { selectWidth } from './storeSlices/windowWidthSlice.js';
import NewsSlider from './NewsSlider';
import NewsWithoutSlider from './NewsWithoutSlider';
import './styles/News/News.css'
import Title from './UI/Title/Title';
import NewsItemType from '../types/NewsItem';
import axiosRequests from '../classes/axiosRequests.js';
import { fetchAllNews } from './storeSlices/newsSlice/newsAPI.js';

const News = () => {
    const windowWidth = useSelector(selectWidth) as number
    const [NewsList, setNewsList] = useState <NewsItemType[]>([])

    const dispatch = useDispatch()
    useEffect(() => {
        // axiosRequests.GET<NewsItemType>('/api/news')
        // .then(response => setNewsList(response))
        // .catch(e => console.log(e))
        dispatch(() => fetchAllNews('/api/news'))

    }, [])

    return (
        <div className='News'>
             <Title hType='h1'>новости</Title>
            {
                windowWidth > 1000 ?
                <NewsWithoutSlider NewsList={NewsList} /> :
                <NewsSlider NewsList={NewsList.slice(0, 30)}/>
            } 
        </div>
    );
};

export default News;
