import React, { FC, useEffect, useState } from 'react';
import './styles/Trigger.css'
import Button, { ButtonBorderVariant, ButtonTextVariant } from './UI/Button/Button';

interface TriggerProps{
    imgPath: string;
    title: string;
    buttonText: string;
    buttonTextTwo?: string;
    titles: string[];
}
interface WHObject{
    name: string;
    width: {w1: number,w2: number ,w3: number,w4: number,w5: number};
    height: {h1: number,h2: number ,h3: number,h4: number,h5: number};
}



const Trigger: FC<TriggerProps> = ({imgPath, title, buttonText, buttonTextTwo, titles}) => {
    const [isHover, setIsHover] = useState(false)
    const [windowWidth, setWindowWidth] = useState <number>(window.innerWidth)

    // w1 === 1440-1000; w2 === 1000-768; w3 === 768-480; w4 === 480-320; w5 === > 320    <<============ height too

    let TriggerWH = [
        ///////////////////
        {name: 'защита', 

        width:
        {
            w1: 130, w2: 100,
            w3: 58, w4: 58,
            w5: 58
        },
        height :
        {
            h1: 157, h2: 121,
            h4: 70, h3: 70,
            h5: 70
        }

        },
        ///////////////////

        {name: 'твоё Будущее',

        width:
        {
            w1: 158, w2: 116,
            w3: 60, w4: 60,
            w5: 60
        },
        height :
        {
            h1: 158, h2: 116,
            h4: 60, h3: 60,
            h5: 60
        }

        },
        ///////////////////

        {name: 'Бизнес предложения',

        width:
        {
            w1: 187, w2: 118,
            w3: 62, w4: 62,
            w5: 62
        },
        height :
        {
            h1: 157, h2: 99,
            h4: 49, h3: 49,
            h5: 49
        }

        },
    ]
    const mouseOverHandler = (e: React.MouseEvent<HTMLDivElement>) => {
        setIsHover(true)
    }
    const mouseOutHandler = (e: React.MouseEvent<HTMLDivElement>) => {
        setIsHover(false)
    }
    function WidthHeightStylesReturn(nnArg:  WHObject[], WHType: 'width' | 'height'): number {
        let retVal = 0
        nnArg.forEach(cv => {
            if(cv.name === title){
                if(WHType === 'width'){
                    if(windowWidth <= 1000 && windowWidth > 768){
                        retVal = cv.width.w2
                    }
                    else if(windowWidth <= 768 && windowWidth > 480){
                        retVal = cv.width.w3
                    }
                    else if(windowWidth <= 480 && windowWidth > 320){
                        retVal = cv.width.w4
                    }
                    else if(windowWidth <= 320){
                        retVal = cv.width.w5
                    }
                    else {
                        retVal = cv.width.w1
                    }
                }
                else if(WHType === 'height'){
                    if(windowWidth <= 1000 && windowWidth > 768){
                        retVal = cv.height.h2
                    }
                    else if(windowWidth <= 768 && windowWidth > 480){
                        retVal = cv.height.h3
                    }
                    else if(windowWidth <= 480 && windowWidth > 320){
                        retVal = cv.height.h4
                    }
                    else if(windowWidth <= 320){
                        retVal = cv.height.h5
                    }
                    else {
                        retVal = cv.height.h1
                    }
                }
            }
        })
        return retVal
    }
    window.addEventListener('resize', () => {
        setWindowWidth(window.innerWidth)
    }, true);

    return (
        <div
            className={isHover ? 'TriggerDirty' : 'Trigger'}
            onMouseOver={mouseOverHandler}
            onMouseOut={mouseOutHandler}
        >   
           <div className="Trigger__elements-container">
            <div className="Img_and_Title">
            <h1 className="Trigger__title"
            style={{marginTop: title.trim().split(/\s+/).length >= 2 ? '50px' : '74px'}}
            >{title}</h1>
            <div
            className="Trigger__image-cont"
            style={{width: WidthHeightStylesReturn(TriggerWH, 'width'),
            // Костыль!
            height:  WidthHeightStylesReturn(TriggerWH, 'height')}} 
            >   
                <img src={imgPath} alt="f" />   
            </div>
            </div>
            <div className="buttons" style={{marginTop: buttonTextTwo ? '63px' : '96px'}}>
                <>
                    <Button
                        text={buttonText}
                        height={50}
                        width={255}
                        variantBorder={isHover ? ButtonBorderVariant.dirty : ButtonBorderVariant.black}
                        variantText={isHover ? ButtonTextVariant.dirty : ButtonTextVariant.black}
                    />
                    {
                    buttonTextTwo ?
                    <div style={{marginTop: '31px'}}>
                        <Button
                        text={buttonTextTwo}
                        height={50}
                        width={255}
                        variantBorder={isHover ? ButtonBorderVariant.dirty : ButtonBorderVariant.black}
                        variantText={isHover ? ButtonTextVariant.dirty : ButtonTextVariant.black}
                        />
                    </div>
                    : () => {}
                    }
                </>
            </div>
           </div>
        </div>
    );
};

export default Trigger;
