import React from 'react';
import './styles/RegistrationForm/RegistrationForm.css'
import { Col, Row } from 'react-bootstrap';
import FormInput from './UI/FormInput/FormInput';


const RegistrationForm = () => {
    return (
        <div className='RegistrationForm' style={{marginTop: '103px'}}>
            <div className="RegistrationForm__wrapper">
                <Row>
                    <Col className='align-center-style'>
                        <FormInput
                        indexOne = {true}
                        placeholder='Иванов'
                        title='Фамилия'
                        />
                        <FormInput
                        placeholder='Иван'
                        title='Имя'
                        />
                        <FormInput
                        placeholder='Иванович'
                        title='Отчество'
                        />
                    </Col>
                    <Col className='align-center-style'>
                        <FormInput
                        indexOne = {true}

                        placeholder=''
                        title='Телефон'
                        validation= {{
                            onlyNums: true,
                        }}
                        isTel={true}
                        />
                        <FormInput
                        placeholder='mail@example.ru'
                        title='E-mail'
                        validation={{
                            length: 6,
                            email: true
                        }}
                        />
                        <FormInput
                        placeholder='Придумайте пароль'
                        title='Пароль'
                        isPassword={true}
                        validation={{
                            length: 5
                        }}
                        />
                        <FormInput
                        placeholder='Повторите пароль'
                        title='Подтверждение пароля'
                        errorMessage='Пароли не совпадают, попробуйте
                        ввести еще раз'
                        />
                    </Col>
                    <Col className='align-center-style'>
                        <div className='dop-info'>
                            На указанные контактные данные придет код для подтверждения регистрации
                        </div>
                        <FormInput
                        placeholder='ДД.ММ.ГГГГ'
                        title='Дата рождения'
                        errorMessage='Введите дату рождения'
                        width={'small'}
                        date={true}
                        validation={{
                            birthday: true
                        }}
                        />
                        <FormInput
                        placeholder='123456'
                        title='Номер карты клиента'
                        errorMessage='Укажите (если имеется) последние шесть цифр'
                        isInfo={true}
                        width={'small'}
                        validation={{
                            card: true
                        }}
                        />
                    </Col>
                </Row>
            </div>
        </div>
    );
};

export default RegistrationForm;
