import React, { FC, useState } from 'react';
import NewsItemType from '../types/NewsItem';
import { Row, Col } from 'react-bootstrap';
import NewsItem from './NewsItem';

interface NewsListBSprops{
    NewsList: NewsItemType[];
}

const NewsListBS:FC<NewsListBSprops> = ({NewsList}) => {
    const updateList = (list: NewsItemType[]): NewsItemType[] => {
        if(list.length % 3 === 0){
            return list
        }
        else if(list.length % 3 === 1){
            return list.slice(list.length - 1, 1)
        }
        else if(list.length % 3 === 2){
            return list.slice(list.length - 2, 2)
        }
        else{
            return list
        }
    }
    const UpdatedNewsList: NewsItemType[] = updateList(NewsList)
    console.log(updateList)

    return (
        <div className='NewsListBS'>
                {   
                    UpdatedNewsList.length !== 0 ?
                    UpdatedNewsList.slice(0, UpdatedNewsList.length / 3).map((item: NewsItemType, index: number) => {
                        const sliceElements = 3
                        let counter: number = 3 * (0 + index)

                        return (
                            <Row>
                                {UpdatedNewsList.slice(counter, sliceElements).map((item: NewsItemType, index: number) => 
                                <Col>
                                    <NewsItem
                                    style={{marginLeft: "0px"}}
                                    key={item._id}
                                    image={item.image}
                                    _id={item._id}
                                    date={item.date}
                                    description={item.description}
                                    subDescription={item.subDescription}
                                    />
                                </Col>

                                )}
                            </Row>
                        )
                    })
                    :
                    <div className='NoNews'>Список новостей пуст...</div> 
                }

        </div>
    );
};

export default NewsListBS;
